import logo from './logo.svg';
import './App.css';
import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowRight } from '@fortawesome/free-solid-svg-icons';
import { BsArrowRight } from 'react-icons/bs';
import { useEffect, useRef } from 'react';

function App() {
  const outerDivRef = useRef(null);
  const innerDivRef = useRef(null);

  useEffect(() => {
    // 获取内部div的宽度
    const innerDivWidth = innerDivRef.current.getBoundingClientRect().width;
    // 将内部div的宽度应用到外部div
    outerDivRef.current.style.width = `${innerDivWidth}px`;
  }, []); // 这个effect只会在组件挂载时运行一次

  return (
    <div className="App">
      <header className="App-header">
        <div style={{width:'100vw'}}>
          <div style={{display:'flex', flexDirection:'row', justifyContent:"space-between", alignItems:'start'}}>
            <div>
              <p className='big-title'>Behind the Covers</p>
              <div className='group1'>
                <p className='date'>11/28（二）</p>
                <div className='time'>
                  <p style={{margin:'0px 0px 12px 4px'}}>10 a.m.</p>
                  {/* <FontAwesomeIcon icon={faArrowRight} /> */}
                  <BsArrowRight />
                  <p style={{margin:'0px 0px 12px 4px'}}>12 p.m.</p>
                </div>
                <p className='time' style={{margin:'0px 0px 12px 4px'}}>政大<span className='place'>研究大樓</span>203</p>
              </div>
            </div>

            <div 
              style={{
                    // transformOrigin:'left', 
                    // transform:'rotate(-270deg)', 
                    writingMode: 'vertical-lr', 
                    textOrientation: 'sideways',
                    display:'flex', 
                    flexDirection:'column',
                    alignItems:'flex-start',
                    }}>
              <p className='year' >©2023</p>
              <p className='title'>一個設計師的水<span style={{fontWeight:'bolder', whiteSpace:'nowrap', textAlign:'left'}}>很深</span></p>
            </div>
          </div>

          {/* <div className='break'></div> */}

          <div className="image-container">
            <img className='head' src='/head.png' />
          </div>

          <div className='break'></div>

          <div style={{display:'flex', flexDirection:'row', justifyContent:'space-between'}}>
            <div className='group1' style={{display:'flex', flexDirection:'row', gap:'16px', alignItems:"flex-end"}}>
              <p className='name'>聶永真</p>
              <div className='english'>
                aaron
                <br />
                nieh
              </div>
            </div>

            <div ref={outerDivRef} style={{ display: 'inline-block', overflow: 'hidden', maxWidth:''}}>
              <div ref={innerDivRef} style={{display:'flex', flexDirection:'row', justifyContent:'space-between'}}>
                <p className='little-title'>Behind the <br/>covers</p>
                <p className='little-subtitle'>一個設計師的水很深</p>
              </div>
              <hr />
              <p className='content' style={{wordBreak:'break-all'}}>
                此場講座將從聶永真的個人視角，分享其團隊近年作品產出的思考觀點、框架、敘事方式及品味決策；偶爾穿插一咪咪（作為一個活生生不是隨時獨立思考都會很漂亮周全的、偶爾螢光、有時低飽和的）個體日常與鬼談百景。
              </p>
            </div>
          </div>
        </div>
      </header>
    </div>
  );
}

export default App;
